wwv_flow_imp_page.create_page_process(
 p_id=>wwv_flow_imp.id(40422367711901002)
,p_process_sequence=>10
,p_process_point=>'ON_SUBMIT_BEFORE_COMPUTATION'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'GetAnswer'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  cnt      number;',
'  locusr   varchar2(50);',
'  stt      timestamp;',
'  st       varchar2(50);',
'  ett      timestamp;',
'  et       varchar2(50);',
'  mst      number;',
'begin',
'',
'  if (:P1_QUESTION = '''') or (:P1_QUESTION is null) then',
'    :P1_ANSWER := ''Please enter a question.'';',
'  else',
'      -- init session context if desired',
'      if (:P1_KEEP_HISTORY = ''Y'') then',
'        :P1_CONTEXT := :P1_SESS_CTX;',
'      else',
'        :P1_CONTEXT := '''';',
'      end if;',
'      ',
'      -- get the latest query params',
'      select count(riab_user) into cnt',
'        from hriab_user_settings',
'       where riab_user = :user',
'         and pref_type = ''EXEC'';',
'',
'      if (cnt = 0) then',
'        locusr := ''DEFAULT'';',
'      else',
'        locusr := :user;',
'      end if;',
'',
'      if (:P1_AUTOGEN_TOKENS = ''Y'') then',
'        hriab_gen_tokens( :P1_QUESTION, :P1_TOKENS, locusr);',
'      end if;',
'',
'      -- get the start timestamp',
'      select systimestamp into stt from dual;',
'      select ''Query started: ''||to_char(stt, ''yyyy/mm/dd hh24:mm:ss:ff3'')||chr(10) into st from dual;',
'',
'      -- get the answer',
'      hriab_get_answer(:P1_QUESTION, :P1_TOKENS, locusr, :P1_ANSWER, :P1_CONTEXT);',
'',
'      -- get the end timestamp',
'      select systimestamp into ett from dual;',
'      select ''Query ended: ''||to_char(ett, ''yyyy/mm/dd hh24:mm:ss:ff3'')||chr(10) into et from dual;',
'',
'      -- millisecs interval',
'      mst := hriab_util_timestamp_diff(stt, ett);',
'',
'      -- store the answers in cumulative way',
'      if (:P1_KEEP_HISTORY = ''Y'') then',
'        :P1_SESS_CTX := :P1_SESS_CTX ||'' ''||:P1_QUESTION||'' ''||:P1_ANSWER;',
'      else',
'        :P1_SESS_CTX := '''';',
'      end if;',
'',
'      -- if desired add timing and index usage text',
'      if (:P1_TIMING = ''Y'') then',
'        :p1_answer := st || ''-----'' || chr(10)',
'                         || :p1_answer || chr(10) || ''-----'' || chr(10)',
'                   || et || ''Total msecs: '' || mst  || chr(10); ',
'      end if;',
'  end if;',
'end;'))
